@media (max-width: 1199px) {
  .minicart--fixed {
    width: 100%;
    height: 100%;
    overflow: hidden;
  }
  .minicart {
    position: fixed;
    z-index: 200;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0,0,0,0.5);
    visibility: hidden;
    opacity: 0;
    transition: all 0.2s ease-in-out;
  }
  .minicart.minicart--actived {
    visibility: visible;
    opacity: 1;
  }
  .minicart__container {
    position: fixed;
    top: 0;
    right: 0;
    width: 300px;
    height: 100%;
    background: #fff;
    transform: translateX(100%);
    transition: all 0.2s ease-in-out;
  }
  .minicart--actived .minicart__container {
    transform: translateX(0);
  }
  .minicart__header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin: 0 10px;
    border-bottom: 1px solid #f3f3f3;
  }
  .minicart__title {
    color: #b53b96;
    flex: 1 1 auto;
    font-size: 14px;
    font-weight: 700;
    margin: 0;
    text-transform: uppercase;
  }
  .minicart__close {
    width: 45px;
    height: 45px;
    display: flex;
    justify-content: center;
    align-items: center;
    color: #b53b96;
    font-size: 20px;
  }
  .minicart__empty {
    font-weight: 500;
    text-align: center;
    padding: 20px;
  }
  .minicart__list {
    margin: 0;
    padding: 0;
  }
  .minicart__item {
    position: relative;
    max-height: 130px;
    visibility: visible;
    opacity: 1;
    transition: all 0.4s ease-in-out;
  }
  .minicart__item + .minicart__item {
    border-top: solid 1px #eee;
  }
  .minicart__link {
    display: flex;
    align-items: center;
    list-style: none;
    padding: 10px;
    color: #666;
  }
  .minicart__link:hover {
    text-decoration: none;
  }
  .minicart__info {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: center;
  }
  .minicart__name {
    font-size: 14px;
    flex: 0 0 100%;
  }
  .minicart__image {
    flex: 0 0 50px;
    margin-right: 20px;
  }
  .minicart__qty {
    font-size: 10px;
  }
  .minicart__price {
    font-size: 14px;
    color: #f00;
  }
  .minicart__total {
    display: flex;
    height: 45px;
    align-items: center;
    justify-content: space-between;
    font-size: 14px;
    font-weight: 700;
    padding: 0 20px;
  }
  .minicart__main {
    height: calc(100% - 140px);
    overflow-y: auto;
  }
  .minicart__finish {
    background: #3aad27;
    color: #fff;
    font-size: 16px;
    font-weight: 700;
    text-transform: uppercase;
    height: 50px;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .minicart__finish:hover {
    background: #339021;
  }
  .minicart__delete {
    background: #fff;
    border: 1px solid #e74949;
    border-radius: 3px;
    font-size: 10px;
    font-weight: 600;
    height: 25px;
    line-height: 25px;
    margin: 0;
    max-height: 0;
    opacity: 0;
    overflow: hidden;
    padding: 0 10px;
    transition: all 150ms ease-in-out;
    visibility: hidden;
  }
  .minicart__delete svg {
    fill: #e74949;
  }
  .minicart__delete:hover {
    background: #e74949;
    color: #fff;
  }
  .minicart__delete:hover svg {
    fill: #fff;
  }
  .minicart__qty:before {
    content: ' / ';
  }
}
@media (min-width: 1200px) {
  #cart .dropdown:hover .cart__dropdown {
    opacity: 1;
    visibility: visible;
  }
  .minicart__list {
    border-bottom: 1px solid #ededed;
    margin-bottom: 30px;
    max-height: 250px;
    overflow: auto;
  }
  .minicart__item {
    border-bottom: 1px solid #ededed;
    padding: 25px 0;
  }
  .minicart__item:last-child {
    border-bottom: 0 none;
  }
  .minicart__link {
    align-items: center;
    display: flex;
  }
  .minicart__image {
    align-items: center;
    display: flex;
    flex: 0 0 auto;
    justify-content: center;
    margin-right: 15px;
    position: relative;
    width: 70px;
  }
  .minicart__image img {
    max-height: 60px;
    max-width: 60px;
  }
  .minicart__info {
    flex: 1 1 auto;
  }
  .minicart__name {
    color: #767767;
    display: block;
    font-size: 13px;
    font-weight: 600;
    line-height: 12px;
    margin: 0 0 5px;
    text-transform: uppercase;
  }
  .minicart__details {
    align-items: baseline;
    display: flex;
    justify-content: space-between;
    margin-bottom: 10px;
  }
  .minicart__price,
  .minicart__qty {
    color: #e2b22b;
    font-size: 13px;
    font-weight: 600;
    line-height: 13px;
  }
  .minicart__total-label {
    color: #403232;
    display: block;
    font-size: 13px;
    font-weight: 600;
    line-height: 13px;
    margin-bottom: 2px;
    text-align: center;
    text-transform: uppercase;
  }
  .minicart__total-price {
    color: #e2b22b;
    display: block;
    font-size: 16px;
    font-weight: 600;
    line-height: 16px;
    text-align: center;
  }
  .minicart__finish {
    background: #75da5b;
    border: 1px solid #75da5b;
    border-radius: 3px;
    box-shadow: 2px 2px 10px 0 rgba(92,19,30,0.15);
    color: #fff;
    display: block;
    font-size: 16px;
    font-weight: 600;
    line-height: 16px;
    margin-top: 25px;
    padding: 16px 20px;
    text-align: center;
    text-transform: uppercase;
    width: auto;
  }
  .minicart__finish:hover {
    background: #fff;
    color: #75da5b;
  }
}
.minicart__header {
  display: none;
}
.cart__dropdown {
  position: absolute;
  right: 0;
  background: #fff;
  z-index: 99999999;
  width: 340px;
  padding: 30px;
  border: 1px solid #000;
  top: 100%;
  display: none;
}
.cart {
  position: relative;
}
.cart:hover .cart__dropdown {
  display: block;
}
